var modalNotice=document.querySelector(".modal-buying-notice"),buyLink=document.querySelectorAll(".button-buying"),closeNotice=document.querySelector(".closing-button");buyLink.forEach(function(o){o.addEventListener("click",function(o){o.preventDefault(),console.log("Открой модальное окно"),modalNotice.classList.add("popup-show")})}),closeNotice.addEventListener("click",function(o){o.preventDefault(),console.log("Закрой модальное окно"),modalNotice.classList.remove("popup-show")}),window.addEventListener("keydown",function(o){27===o.keyCode&&modalNotice.classList.contains("popup-show")&&(o.preventDefault(),console.log("Закрой модальное окно кнопкой ESC"),modalNotice.classList.remove("popup-show"))});